import{_ as s,o as e,c as a,R as o}from"./chunks/framework.318fa179.js";const y=JSON.parse('{"title":"PostgreSQL pg_sleep_for() 函数","description":"","frontmatter":{},"headers":[],"relativePath":"database/postgreSQL/ref/pg_sleep_for.md","filePath":"database/postgreSQL/ref/pg_sleep_for.md","lastUpdated":1690159250000}'),p={name:"database/postgreSQL/ref/pg_sleep_for.md"},l=o(`<h1 id="postgresql-pg-sleep-for-函数" tabindex="-1">PostgreSQL pg_sleep_for() 函数 <a class="header-anchor" href="#postgresql-pg-sleep-for-函数" aria-label="Permalink to &quot;PostgreSQL pg\\_sleep\\_for() 函数&quot;">​</a></h1><p>PostgreSQL <code>pg_sleep_for()</code> 函数接收一个间隔参数，将当前服务器进程的执行暂停（睡眠）指定的间隔。</p><h2 id="pg-sleep-for-语法" tabindex="-1"><code>pg_sleep_for()</code> 语法 <a class="header-anchor" href="#pg-sleep-for-语法" aria-label="Permalink to &quot;\`pg_sleep_for()\` 语法&quot;">​</a></h2><p>这里是 PostgreSQL <code>pg_sleep_for()</code> 函数的语法：</p><div class="language-sql"><button title="Copy Code" class="copy"></button><span class="lang">sql</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">pg_sleep_for(duration INTERVAL)</span></span></code></pre></div><h3 id="参数" tabindex="-1">参数 <a class="header-anchor" href="#参数" aria-label="Permalink to &quot;参数&quot;">​</a></h3><ul><li><p><em><code>duration</code></em></p><p>必需的。一个 <code>INTERVAL</code> 类型的值， 它指示了要将当前服务器进程的执行暂停多久。</p></li></ul><h3 id="返回值" tabindex="-1">返回值 <a class="header-anchor" href="#返回值" aria-label="Permalink to &quot;返回值&quot;">​</a></h3><p>PostgreSQL <code>pg_sleep_for()</code> 函数没有返回值，它用于将当前服务器进程的执行暂停（睡眠）指定的间隔。</p><h2 id="pg-sleep-for-示例" tabindex="-1"><code>pg_sleep_for()</code> 示例 <a class="header-anchor" href="#pg-sleep-for-示例" aria-label="Permalink to &quot;\`pg_sleep_for()\` 示例&quot;">​</a></h2><p>这里结合 <a href="./clock_timestamp.html"><code>clock_timestamp()</code></a> 函数展示了 <code>pg_sleep_for()</code> 的能力。</p><div class="language-sql"><button title="Copy Code" class="copy"></button><span class="lang">sql</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#F78C6C;">SELECT</span></span>
<span class="line"><span style="color:#A6ACCD;">    clock_timestamp</span><span style="color:#89DDFF;">()</span><span style="color:#A6ACCD;">,</span></span>
<span class="line"><span style="color:#A6ACCD;">    pg_sleep_for(</span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">10 seconds</span><span style="color:#89DDFF;">&#39;</span><span style="color:#A6ACCD;">),</span></span>
<span class="line"><span style="color:#A6ACCD;">    clock_timestamp</span><span style="color:#89DDFF;">()</span><span style="color:#A6ACCD;">;</span></span></code></pre></div><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">-[</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">RECORD</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">1</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">]---+------------------------------</span></span>
<span class="line"><span style="color:#FFCB6B;">clock_timestamp</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">|</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">2022-05-20</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">11</span><span style="color:#C3E88D;">:31:05.691725+08</span></span>
<span class="line"><span style="color:#FFCB6B;">pg_sleep_for</span><span style="color:#A6ACCD;">    </span><span style="color:#89DDFF;">|</span></span>
<span class="line"><span style="color:#FFCB6B;">clock_timestamp</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">|</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">2022-05-20</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">11</span><span style="color:#C3E88D;">:31:15.695237+08</span></span></code></pre></div><p>这里:</p><ul><li>我们对 <code>pg_sleep_for()</code> 使用了一个间隔值： <code>10 seconds</code>，它用于将当前服务器进程的执行暂停 10 秒。</li><li>由于我们在两个 <code>clock_timestamp()</code> 函数中间使用 <code>pg_sleep_for(&#39;10 seconds&#39;)</code> 将当前服务器进程的执行暂定了 10 秒，所以第二个 <code>clock_timestamp()</code> 函数比第一个的执行晚了 10 秒，从而第二个的返回值比第一个晚 10 秒。</li></ul>`,15),n=[l];function t(c,r,d,i,_,C){return e(),a("div",null,n)}const h=s(p,[["render",t]]);export{y as __pageData,h as default};
