import{_ as s,o,c as a,R as l}from"./chunks/framework.333b51ca.js";const F=JSON.parse('{"title":"PostgreSQL polygon(box) 函数","description":"","frontmatter":{},"headers":[],"relativePath":"database/postgreSQL/ref/polygon(box).md","filePath":"database/postgreSQL/ref/polygon(box).md","lastUpdated":1690352360000}'),n={name:"database/postgreSQL/ref/polygon(box).md"},p=l('<h1 id="postgresql-polygon-box-函数" tabindex="-1">PostgreSQL polygon(box) 函数 <a class="header-anchor" href="#postgresql-polygon-box-函数" aria-label="Permalink to &quot;PostgreSQL polygon(box) 函数&quot;">​</a></h1><p>PostgreSQL <code>polygon(box)</code> 函数将一个矩形转成一个由四个点表示的多边形并返回。</p><h2 id="polygon-box-语法" tabindex="-1"><code>polygon(box)</code> 语法 <a class="header-anchor" href="#polygon-box-语法" aria-label="Permalink to &quot;`polygon(box)` 语法&quot;">​</a></h2><p>这是 PostgreSQL <code>polygon(box)</code> 函数的语法：</p><div class="language-sql"><button title="Copy Code" class="copy"></button><span class="lang">sql</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#C792EA;">polygon</span><span style="color:#A6ACCD;">(</span><span style="color:#C792EA;">box</span><span style="color:#A6ACCD;">) </span><span style="color:#89DDFF;">-&gt;</span><span style="color:#A6ACCD;"> </span><span style="color:#C792EA;">polygon</span></span></code></pre></div><h3 id="参数" tabindex="-1">参数 <a class="header-anchor" href="#参数" aria-label="Permalink to &quot;参数&quot;">​</a></h3><ul><li><p><em><code>box</code></em></p><p>必需的。 一个矩形。例如： <code>box &#39;(1,1),(-1,-1)&#39;</code></p></li></ul><h3 id="返回值" tabindex="-1">返回值 <a class="header-anchor" href="#返回值" aria-label="Permalink to &quot;返回值&quot;">​</a></h3><p>PostgreSQL <code>polygon(box)</code> 函数返回一个由参数指定的矩形转成的由四个点表示的多边形。</p><h2 id="polygon-box-示例" tabindex="-1"><code>polygon(box)</code> 示例 <a class="header-anchor" href="#polygon-box-示例" aria-label="Permalink to &quot;`polygon(box)` 示例&quot;">​</a></h2><p>下面的语句示例展示了如何使用 PostgreSQL <code>polygon(box)</code> 函数将矩形 <code>box &#39;(1,1),(-1,-1)&#39;</code> 转为多边形。</p><div class="language-sql"><button title="Copy Code" class="copy"></button><span class="lang">sql</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#F78C6C;">SELECT</span><span style="color:#A6ACCD;"> </span><span style="color:#C792EA;">polygon</span><span style="color:#A6ACCD;">(</span><span style="color:#C792EA;">box</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">(1,1),(-1,-1)</span><span style="color:#89DDFF;">&#39;</span><span style="color:#A6ACCD;">);</span></span></code></pre></div><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">polygon</span></span>\n<span class="line"><span style="color:#FFCB6B;">-------------------------------</span></span>\n<span class="line"><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">((-</span><span style="color:#F78C6C;">1</span><span style="color:#89DDFF;">,-</span><span style="color:#F78C6C;">1</span><span style="color:#A6ACCD;">)</span><span style="color:#89DDFF;">,</span><span style="color:#A6ACCD;">(</span><span style="color:#89DDFF;">-</span><span style="color:#F78C6C;">1</span><span style="color:#89DDFF;">,</span><span style="color:#F78C6C;">1</span><span style="color:#A6ACCD;">)</span><span style="color:#89DDFF;">,</span><span style="color:#A6ACCD;">(</span><span style="color:#F78C6C;">1</span><span style="color:#89DDFF;">,</span><span style="color:#F78C6C;">1</span><span style="color:#A6ACCD;">)</span><span style="color:#89DDFF;">,</span><span style="color:#A6ACCD;">(</span><span style="color:#F78C6C;">1</span><span style="color:#89DDFF;">,-</span><span style="color:#F78C6C;">1</span><span style="color:#89DDFF;">))</span></span></code></pre></div><p>下面的语句示例展示了如何使用 PostgreSQL <code>polygon(box)</code> 函数将矩形 <code>box &#39;(2,2),(1,1)&#39;</code> 转为多边形。</p><div class="language-sql"><button title="Copy Code" class="copy"></button><span class="lang">sql</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#F78C6C;">SELECT</span><span style="color:#A6ACCD;"> </span><span style="color:#C792EA;">polygon</span><span style="color:#A6ACCD;">(</span><span style="color:#C792EA;">box</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">(2,2),(1,1)</span><span style="color:#89DDFF;">&#39;</span><span style="color:#A6ACCD;">);</span></span></code></pre></div><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">polygon</span></span>\n<span class="line"><span style="color:#FFCB6B;">---------------------------</span></span>\n<span class="line"><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">((</span><span style="color:#F78C6C;">1</span><span style="color:#89DDFF;">,</span><span style="color:#F78C6C;">1</span><span style="color:#A6ACCD;">)</span><span style="color:#89DDFF;">,</span><span style="color:#A6ACCD;">(</span><span style="color:#F78C6C;">1</span><span style="color:#89DDFF;">,</span><span style="color:#F78C6C;">2</span><span style="color:#A6ACCD;">)</span><span style="color:#89DDFF;">,</span><span style="color:#A6ACCD;">(</span><span style="color:#F78C6C;">2</span><span style="color:#89DDFF;">,</span><span style="color:#F78C6C;">2</span><span style="color:#A6ACCD;">)</span><span style="color:#89DDFF;">,</span><span style="color:#A6ACCD;">(</span><span style="color:#F78C6C;">2</span><span style="color:#89DDFF;">,</span><span style="color:#F78C6C;">1</span><span style="color:#89DDFF;">))</span></span></code></pre></div>',16),e=[p];function t(c,r,y,C,d,i){return o(),a("div",null,e)}const g